// ignore_for_file: avoid_dynamic_calls, inference_failure_on_untyped_parameter

import '../extensions/extensions.dart';

import 'stock_record.dart';

class Stock {
  Stock({
    required this.cost,
    required this.price,
    required this.diff,
    required this.date,
    required this.record,
  });

  factory Stock.fromJson(Map<String, dynamic> json) => Stock(
        cost: json['cost'].toString().toInt(),
        price: json['price'].toString().toInt(),
        diff: json['diff'].toString().toInt(),
        date: json['date'].toString().toDateTime(),
        record: List<StockRecord>.from(json['record']
                .map((x) => StockRecord.fromJson(x as Map<String, dynamic>))
            as Iterable),
      );

  int cost;
  int price;
  int diff;
  DateTime date;
  List<StockRecord> record;

  Map<String, dynamic> toJson() => {
        'cost': cost,
        'price': price,
        'diff': diff,
        'date':
            "${date.year.toString().padLeft(4, '0')}-${date.month.toString().padLeft(2, '0')}-${date.day.toString().padLeft(2, '0')}",
        'record': List<dynamic>.from(record.map((x) => x.toJson())),
      };
}
